<?php

namespace AppBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * WallpaperRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class WallpaperRepository extends EntityRepository
{
		/****
	returns the number of entity's rows
	@return int
	*/
	public function countAll() {
		$query = $this->createQueryBuilder('e')->select('count(e)')->where("e.review = false")->getQuery();
		return $query->getSingleScalarResult();
	}
	public function count($type) {
		$query = $this->createQueryBuilder('e')->select('count(e)')->where("e.type like '".$type."'")->getQuery();
		return $query->getSingleScalarResult();
	}

	public function countReview() {
		$query = $this->createQueryBuilder('e')->select('count(e)')->where("e.review = true")->getQuery();
		return $query->getSingleScalarResult();
	}
	public function countDownloads() {
		$query = $this->createQueryBuilder('e')->select('sum(e.downloads)')->getQuery();
		return $query->getSingleScalarResult();
	}
	public function countShares() {
		$query = $this->createQueryBuilder('e')->select('sum(e.shares)')->getQuery();
		return $query->getSingleScalarResult();
	}
	public function countSets() {
		$query = $this->createQueryBuilder('e')->select('sum(e.sets)')->getQuery();
		return $query->getSingleScalarResult();
	}
	public function countViews() {
		$query = $this->createQueryBuilder('e')->select('sum(e.views)')->getQuery();
		return $query->getSingleScalarResult();
	}
}
